/* Device utilities:.    FLOPPY (level)              		- returns TRUE if the specified LEVEL is a floppy.    WINCHESTER (level)          		- returns TRUE if the specified LEVEL is a Winchester.    TAPE (level)                		- returns TRUE if the specified LEVEL is a tape.    OPTICAL (level)             		- returns TRUE if the specified LEVEL is an optical.    TRIGGER_MOTOR (level)       		- triggers the motor on the specified floppy.    DISK_CHECK (level)          		- checks if a disk is in the specified drive.    GET_DEVICE (devname, tape)  		- returns level of DEVNAME; ignores tapes if TAPE is false.    FIND_TYPE (D107_#, drive)   		- polls IMI Winchester to determine its type.    POLL_WINCHESTERS (config, len) 	- returns IMI Winchester configuration (LEN is length of CONFIG).    FIND_OPTICAL (devadr)       		- detects what type of optical drive is on system.    FIND_WIN_SIZE (devadr, cap, inq) 	- stores capacity and inquiry info of given SCSI winchester in config area provided.    POLL_SCSI (config, len)     		- returns SCSI configuration (LEN is length of CONFIG)*/#define	d_protect			0x0002										// set in value returned by DISK_CHECK if disk is write-protected#define	inq_buf_size_bytes	44											// required size for FIND_WIN_SIZE inq data buffer#define	nam_buf_size_bytes	24											// buffer into which to retrieve scsi nameextern	boolean	floppy(fixed);											// return TRUE if the specified LEVEL is a floppyextern	boolean	winchester(fixed);										// return TRUE if the specified LEVEL is a winchesterextern	boolean	tape(fixed);											// return TRUE if the specified LEVEL is a tapeextern	boolean	optical(fixed);											// return TRUE if the specified LEVEL is an opticalextern	void	trigger_motor(fixed);									// start up floppy motorextern	boolean	disk_check(fixed);										// check for a disk in LEVELextern	fixed	get_device(fixed[], boolean);							// accept a device specifier and return its level numberextern	void	find_type(fixed, fixed);								// find type code of winchesterextern	void	poll_winchesters(fixed[], fixed);						// returns list of Winchesters in the systemextern	fixed	find_optical(fixed);									// find type of optical device on systemextern	fixed	find_win_size(fixed, fixed, array, array);				// find size of SCSI winchesterextern	void	poll_scsi(fixed[], fixed);								// returns list of SCSI Winchesters in the systemextern	void	fetch_inq_name(fixed, array);							// fetch device name via inquiryextern	void	fetch_level_name(fixed, array);							// fetch name of level via inquiryextern	fixed	chain_prob_adr;extern	fixed	set_win_chain_size(fixed);extern	data	win_siz;												// size (in cylinders) of Winchestersextern	data	win_cyl;												// sectors/cylinders of Winchestersextern	data	maxi_config;											// maxifloppy  configextern	data	mini_config;											// minifloppy  configextern	data	dmin_config;											// double mini configextern	data	smin_config;											// super mini config