/* 	FILE:	AddressTypes.h	Describes the sizes and possible values of address elements in address-based	messages between Mac and Audiola.		REVISION HISTORY	----------------	01-MAY-1992		Doug Hill			Created this module.			*//* * These are the pieces used in constructing addresses in messages of interest * to the FILE task. */typedef struct {	uint8 Name[12];} Address_Name;typedef union {	uint16 Node;								/* Mac, Baker, Audiola, etc.  */	uint16 NodeThang;							/* Users or Filesys			  */	uint16 MacType;								/* Only time now			  */	uint16 FileSys;								/* Winchester, etc.			  */	uint16 Winchno;								/* 0, 1, . . .				  */	uint16 FileType;							/* TakeFile, ChangeFile		  */	Address_Name PhileName;						/* pHILE name				  */	uint16 FileSection;							/* AdminFixed, etc.			  */	uint32 DataPos;								/* Byte location in file	  */	uint16 ChangeNo;							/* Change Number			  */} Address_element;enum NodeTypes {	A_MAC_TYPE = 1,	A_BAKER_TYPE = 2,	A_AUDIOLA_TYPE = 3};enum NodeThangTypes {	A_USERS_TYPE = 1,	A_FILESYS_TYPE = 2};enum FileSysTypes {	A_WINCHESTER = 1,	A_OPTICAL = 2};enum FileTypes {	A_TAKEFILE = 1,	A_CHANGEFILE = 2};enum FileSectionTypes {	A_ADMINFIXED = 1,	A_ADMINVAR = 2,	A_AUDIO = 3};enum MacTypes {	M_TIMETYPE =1};