// Message tag definitions#ifndef NED__MESSAGETAGS#define NED__MESSAGETAGS// Message tags are encoded with information that allows Synclavier¨ PowerPCª to route// a response to the correct connection (e.g. a D115 in the able bin, or a PPC// session.// These constants describe the format for the message tags#define	MESSAGE_TAG_NONZERO_BIT		0x0001				// this bit is always nonzero; will allow detection of old-style tags#define	MESSAGE_TAG_FIELD_MASK		0x03FF				// 10-bit message tag field#define	MESSAGE_TAG_SESSION_MASK	0x000F				//  4-bit session field#define	MESSAGE_TAG_SNAP_BIT		0x8000				// bit distingues snap-shot VS non-snap tags#define	MESSAGE_TAG_FIELD_SHIFT		1					// shift position for message tag field#define	MESSAGE_TAG_SESSION_SHIFT   11					// shift position for session identifierenum 	{SESSION_NO_ID, SESSION_BOARD_0, SESSION_PPC};	// indicates a PPC session#endif				/* NED__MESSAGETAGS */